import { run as jscodeshift } from 'jscodeshift/src/Runner.js';

export const transform = async (paths, transformPath) => {
  const options = {
    dry: false,
    print: false,
    verbose: 1,
    extensions: 'js,ts',
    babel: false,
    parser: 'ts'
    // ...
  };

  const res = await jscodeshift(transformPath, paths, options);
  console.log(res);
};

