module.exports = function (file, api, options) {
  const j = api.jscodeshift;

  const deprecatedIcons = {
    "omni:informative:audience": "omni:object:audience",
    "omni:informative:flag": "omni:object:campaign",
    "omni:informative:path": "omni:object:workflow",
    "omni:informative:tv": "omni:app:tvContent"
  };

  return j(file.source)
    .find(j.TemplateElement)
    .forEach(path => {
      Object.entries(deprecatedIcons).forEach(([key, value]) => {
        path.value.value.raw = path.value.value.raw.replaceAll(key, value);
      });
    })
    .toSource();
};
